package elecage.fun_robot.test;

public class Data07 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char x;
		x = 'A';
		System.out.printf("%c -> %d \n", x, (int)x);
		x = '0';
		System.out.printf("%c -> %d \n", x, (int)x);
		x = 0;
		System.out.printf("%c -> %d \n", x, (int)x);
		x = 'a';
		System.out.printf("%c -> %d \n", x, (int)x);
	}

}
