package elecage.fun_robot.test;

public class Opr2 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 4;
		boolean test;
		test = a > b;
		System.out.println(a + " > " + b + " = " + test);
		test = a < b;
		System.out.println(a + " < " + b + " = " + test);
		
		System.out.println(a+b > a-b);
	}

}
