package elecage.fun_robot.test;

public class While01 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		for(i = 1 ; i <= 5 ; i++)
			System.out.print(" " + i);
		System.out.println("\n----------------------------");
		
		i = 1 ; 
		while(i <= 5){
			System.out.print(" " + i);
			i++;
		}
	}

}
