package elecage.fun_robot.test;

public class For06 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i;
		int total = 0;
		
		for(i = 1 ; i <= 5 ; i++)
			total += i;
		
		System.out.println("1 ~ "  + (i-1) + " = "  + total);
	}

}
