package elecage.fun_robot.test;

public class Opr01 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 10, b = 4, c;
		c = a + b;
		System.out.println( a + " + " + b +  " = " + c);
		c = a - b;
		System.out.println( a + " - " + b +  " = " + c);
		c = a * b;
		System.out.println( a + " * " + b +  " = " + c);
		c = a / b;
		System.out.println( a + " / " + b +  " = " + c);
		c = a % b;
		System.out.println( a + " % " + b +  " = " + c);
	}

}
