package elecage.fun_robot.test;

public class Opr06 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch = 'b';
		String s;
		
		s = (ch>='A' && ch <='Z') ? "대문자임" : "대문자 아님";
		System.out.println(ch + " => " + s);
	}

}
