package elecage.fun_robot.test;

public class While03 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int i = 0 ;
		while(i++ <= 4)
			System.out.print(i + ", ");
		System.out.println("\n--------------------->>");
		
		i = 1;
		do
			System.out.print(i + ", ");
		while(i++ <= 4);
		
	
	}

}
